    }
    <span>#[test]</span>
    <span><span>fn</span> <span>remove_element_from_array</span></span>() {
        <span>let</span> data: <span>Vec</span>&lt;<span>i32</span>&gt; = <span>vec!</span>[<span>1</span>, <span>4</span>, <span>5</span>];
