        <span>let</span> <span>mut</span> arr = ds::Array::new(<span>0</span>, data);
        arr.delete_at(<span>1</span>);
        <span>assert_ne!</span>(*arr.get_element(<span>1</span>), <span>2</span>);
    }
}
