</code></pre><h4 id="array">Array</h4>
<pre><code><span>mod</span> ds {
    <span>pub</span> <span><span>struct</span> <span>Array</span></span>&lt;T&gt; {
        <span>pub</span> length: <span>usize</span>,
        data: <span>Vec</span>&lt;T&gt;,
    }
    <span>impl</span>&lt;T: <span>Clone</span>&gt; Array&lt;T&gt; {
        <span>pub</span> <span><span>fn</span> <span>new</span></span>(length: <span>usize</span>, data: <span>Vec</span>&lt;T&gt;)-&gt; Array&lt;T&gt; {
            <span>return</span> Array { length, data};
        }
        <span>pub</span> <span><span>fn</span> <span>push</span></span>(&amp;<span>mut</span> <span>self</span>, elem: T) {
            <span>self</span>.data.push(elem);
            <span>self</span>.calculate_length();
        }
        <span>pub</span> <span><span>fn</span> <span>pop</span></span>(&amp;<span>mut</span> <span>self</span>) {
            <span>self</span>.data.pop();
            <span>self</span>.calculate_length();
        }
        <span>pub</span> <span><span>fn</span> <span>get_element</span></span>(&amp;<span>self</span>, index: <span>usize</span>)-&gt; &amp;T {
            <span>return</span> &amp;<span>self</span>.data[index];
        }
        <span>pub</span> <span><span>fn</span> <span>insert_at</span></span>(&amp;<span>mut</span> <span>self</span>, index: <span>usize</span>, new_element: T) {
            <span>let</span> <span>mut</span> data: <span>Vec</span>&lt;T&gt; = <span>vec!</span>[];
            <span>for</span> (position, element) <span>in</span> <span>self</span>.data.iter().enumerate() {
                <span>if</span> position == index {
                    data.push(new_element.clone());
                }
                data.push(element.clone());
            }
            <span>self</span>.data = data;
            <span>self</span>.calculate_length();
        }
        <span>pub</span> <span><span>fn</span> <span>delete_at</span></span>(&amp;<span>mut</span> <span>self</span>, index: <span>usize</span>){
            <span>let</span> <span>mut</span> data: <span>Vec</span>&lt;T&gt; = <span>vec!</span>[];
            <span>for</span> (position, elem) <span>in</span> <span>self</span>.data.iter().enumerate() {
                <span>if</span> position != index {
                    data.push(elem.clone());
                }
            }
            <span>self</span>.data = data;
            <span>self</span>.calculate_length();
        }
        <span><span>fn</span> <span>calculate_length</span></span>(&amp;<span>mut</span> <span>self</span>) {
            <span>self</span>.length = <span>self</span>.data.len();
        }

    }
}
