<span>int</span> matches_one_of_these_characters(<span>char</span> c, <span>int</span> num_chars, ...){
  <span>va_list</span> arglist;
  va_start(arglist, num_chars);
  <span>int</span> i;
  <span>int</span> found = <span>0</span>;
