    Vec2 v{<span>1</span>, <span>2</span>};
    takeVec2ByValue(v);
}

<span>void</span> <span>callVec2ByPointer</span>() {
    Vec2 v{<span>1</span>, <span>2</span>};
    takeVec2ByPointer(<span>&amp;</span>v);
}

<span>void</span> <span>callVec2ByConstRef</span>() {
    Vec2 v{<span>1</span>, <span>2</span>};
    takeVec2ByConstRef(v);
}

