	try {
		static const auto r = std::regex(R"(\)"); // Escape sequence error
	} catch (const std::regex_error &amp;e) {
		assert(strcmp(e.what(), "Unexpected end of regex when escaping.") == 0);
		assert(e.code() == std::regex_constants::error_escape);
	}
	return EXIT_SUCCESS;
